export function resultReducer(result, action) {
   let res
   let a = action.a
   let b = action.b
   switch (action.type) {
      case '+': {
         res = a + b
         return res
      }
      case '-': {
         res = a - b
         return res
      }
      case 'x': {
         res = a * b
         return res
      }
      case '/': {
         res = a / b
      }
   }
}
