import Button from "./UI/button/Button"

export default function Buttons({ setProcess, a, setA, s, setS, b, setB, dispatch }) {
   
   let aNum
   let bNum

   function handleCalculation(e) {
      setProcess('calculation')
      if (!b) aNum += e.target
      if (s) setB(e.target.value)
   }
   function handleAction(e) {
      if (a) setS(e.target.value)
   }
   function handleCleaning() {
      setProcess('nothing')
      setA('')
      setS('')
      setB('')
   }
   function handleResult() {
      dispatch({
         type: s,
         a: a,
         b: b
      })
      setProcess('result')
   }

   return (
      <div className="buttons">
         <Button onClick={handleCleaning} value='C' butType='sign' />
         <Button onClick={handleAction} value='+/-' butType='sign' />
         <Button onClick={handleAction} value='%' butType='sign' />
         <Button onClick={handleAction} value='/' butType='sign' />
         <Button onClick={handleCalculation} value='7' butType='num' />
         <Button onClick={handleCalculation} value='8' butType='num' />
         <Button onClick={handleCalculation} value='9' butType='num' />
         <Button onClick={handleAction} value='x' butType='sign' />
         <Button onClick={handleCalculation} value='4' butType='num' />
         <Button onClick={handleCalculation} value='5' butType='num' />
         <Button onClick={handleCalculation} value='6' butType='num' />
         <Button onClick={handleAction} value='-' butType='sign' />
         <Button onClick={handleCalculation} value='1' butType='num' />
         <Button onClick={handleCalculation} value='2' butType='num' />
         <Button onClick={handleCalculation} value='3' butType='num' />
         <Button onClick={handleAction} value='+' butType='sign' />
         <Button onClick={handleCalculation} value='0' butType='num' />
         <Button onClick={handleCalculation} value='.' butType='num' />
         <Button onClick={handleAction} value='<' butType='sign' />
         <Button onClick={handleResult} value='=' butType='sign' />
      </div>
   )
}
