import { useState, useReducer, createContext } from 'react'
import Screen from "./Screen"
import Buttons from "./Buttons"
import { resultReducer } from '../reducer'

export default function Calculator() {

   const [result, dispatch] = useReducer(resultReducer, '')
   const [process, setProcess] = useState('nothing')
   const [a, setA] = useState('')
   const [s, setS] = useState('')
   const [b, setB] = useState('')
   const Status = createContext(process)

   return (
      <div className="calculator">
         <Status.Provider value={process}>
            <Screen process={process} a={a} s={s} b={b} result={result} />
         </Status.Provider>
         <Buttons setProcess={setProcess} a={a} setA={setA} s={s} setS={setS} b={b} setB={setB} dispatch={dispatch} />
      </div>
   )
}
