import { useContext } from "react"
import { Status } from "../context"

export default function Screen({ a, s, b, result }) {

   const process = useContext(Status)

   return (
      <div className="screen" style={{ color: dark ? '#fff' : '#008'}}>
         {process === 'result' && result}
         {process === 'calculation' ? a : ''}
         {process === 'calculation' && a ? s : ''}
         {process === 'calculation' && a && s ? b : ''}
      </div>
   )
}
